const translateLabels = (config: any, translateFunc: any) => config;

interface MainConfigViewProps {
    config: ServerConfig;
}

export default function MainConfigView(props: MainConfigViewProps) {
    const {config} = props;
    const services = useServices();
    const translate = useTranslator();
    const {enqueueSnackbar/*, closeSnackbar*/} = useSnackbar();
    const [activeTab, setActiveTab] = useState<string>('api');
    const apiConfig = useMemo<ServerApiConfig>(() => config?.api || {} as any, [config]);
    const mainConfig = useMemo<ServerMainConfig>(() => config, [config]);
    const webUiConfig = useMemo<WebUiConfig>(() => config?.web_ui || {} as any, [config]);
    const webAuthConfig = useMemo<WebAuthConfig>(() => config?.web_ui?.auth || {} as any, [config]);
    const messagingConfig = useMemo<MessagingConfig>(() => config?.messaging || {} as any, [config]);
    const telegramConfig = useMemo<TelegramConfig>(() => config?.messaging?.telegram || {} as any, [config]);
    const restConfig = useMemo<RestConfig>(() => config?.messaging?.rest || {} as any, [config]);
    const pushoverConfig = useMemo<PushoverConfig>(() => config?.messaging?.pushover || {} as any, [config]);
    const videoConfig = useMemo<VideoConfig>(() => config?.video || {} as any, [config]);
    const videoDownloadConfig = useMemo<VideoDownloadConfig>(() => config?.video?.download || {} as any, [config]);
    const reverseProxyConfig = useMemo<ReverseProxyConfig>(() => config?.reverse_proxy || {} as any, [config]);
    const reverseProxyStreamConfig = useMemo<StreamConfig>(() => config?.reverse_proxy?.stream || {} as any, [config]);
    const reverseProxyStreamBufferConfig = useMemo<StreamBufferConfig>(() => config?.reverse_proxy?.stream?.buffer || {} as any, [config]);
    const reverseProxyCacheConfig = useMemo<CacheConfig>(() => config?.reverse_proxy?.cache || {} as any, [config]);
    const reverseProxyRateLimitConfig = useMemo<RateLimitConfig>(() => config?.reverse_proxy?.rate_limit || {} as any, [config]);
    const logConfig = useMemo<LogConfig>(() => config?.log || {} as any, [config]);
    const configs = useMemo(() => {
        CONFIG_WEB_UI_FIELDS.forEach(def => {
            const serverOptions = config?.api_proxy?.server?.map(serverInfo => ({
                value: serverInfo.name,
                label: serverInfo.name
            }));
            if (def.name === "player_server") {
                (def as any).options = serverOptions;
            }
        });
        const translations: any = {
            api_fields: translateLabels(CONFIG_API_FIELDS, translate),
            fields: translateLabels(CONFIG_FIELDS, translate),
            schedules_fields: translateLabels(CONFIG_SCHEDULES_FIELDS, translate),
            log_fields: translateLabels(CONFIG_LOG_FIELDS, translate),
            web_ui_config_fields: translateLabels(CONFIG_WEB_UI_FIELDS, translate),
            web_auth_config_fields: translateLabels(CONFIG_WEB_AUTH_FIELDS, translate),
            reverse_proxy_fields: translateLabels(CONFIG_REVERSE_PROXY_FIELDS, translate),
            reverse_proxy_stream_fields: translateLabels(CONFIG_REVERSE_PROXY_STREAM_FIELDS, translate),
            reverse_proxy_stream_buffer_fields: translateLabels(CONFIG_REVERSE_PROXY_STREAM_BUFFER_FIELDS, translate),
            reverse_proxy_cache_fields: translateLabels(CONFIG_REVERSE_PROXY_CACHE_FIELDS, translate),
            reverse_proxy_rate_limit_fields: translateLabels(CONFIG_REVERSE_PROXY_RATE_LIMIT_FIELDS, translate),
            messaging_fields: translateLabels(CONFIG_MESSAGING_FIELDS, translate),
            telegram_fields: translateLabels(CONFIG_TELEGRAM_FIELDS, translate),
            rest_fields: translateLabels(CONFIG_REST_FIELDS, translate),
            pushover_fields: translateLabels(CONFIG_PUSHOVER_FIELDS, translate),
            video_fields: translateLabels(CONFIG_VIDEO_FIELDS, translate),
            video_download_fields: translateLabels(CONFIG_VIDEO_DOWNLOAD_FIELDS, translate),
            tabs: translateLabels(TABS, translate),
        }
        return translations;
    }, [translate, config?.api_proxy?.server]);

    const scheduleImage = useMemo(() => <svg className={'main-config__content-help-img'} focusable="false"
                                             aria-hidden="true" viewBox='0 0 120 30'>
        <path d={SCHEDULE_IMG_PATH}/>
    </svg>, []);

    const handleSave = useCallback(() => {
        if (mainConfig && apiConfig) {
            let telegramConfigAvailable = telegramConfig?.bot_token?.trim().length && telegramConfig?.chat_ids?.length;
            let restConfigAvailable = restConfig?.url?.trim().length;
            let pushoverConfigAvailable = pushoverConfig?.user?.trim().length && pushoverConfig?.token?.length;
            const cfgMessaging: MessagingConfig = {
                notify_on: messagingConfig.notify_on,
                telegram: telegramConfigAvailable ? getObject(telegramConfig) : undefined,
                rest: restConfigAvailable ? getObject(restConfig) : undefined,
                pushover: pushoverConfigAvailable ? getObject(pushoverConfig) : undefined,
            };


            let reverseProxyStreamConfigAvailable = getObject(reverseProxyStreamConfig) != undefined;
            let cfgReverseProxyStreamBuffer: StreamConfig = reverseProxyStreamConfigAvailable ? ({
                retry: reverseProxyStreamConfig.retry,
                throttle: reverseProxyStreamConfig.throttle?.trim().length ? reverseProxyStreamConfig.throttle : undefined,
                grace_period_millis: reverseProxyStreamConfig.grace_period_millis,
                grace_period_timeout_secs: reverseProxyStreamConfig.grace_period_timeout_secs,
                buffer: {
                    enabled: reverseProxyStreamBufferConfig.enabled,
                    size: reverseProxyStreamBufferConfig.size ?? undefined,
                }
            }): undefined;

            const reverseProxyConfigAvailable = getObject(reverseProxyConfig) ||
                getObject(cfgReverseProxyStreamBuffer) ||
                getObject(reverseProxyCacheConfig) ||
                getObject(reverseProxyRateLimitConfig);
            const cfgReverseProxy: ReverseProxyConfig = reverseProxyConfigAvailable ? {
                resource_rewrite_disabled: reverseProxyConfig.resource_rewrite_disabled,
                stream: getObject(cfgReverseProxyStreamBuffer),
                cache: getObject(reverseProxyCacheConfig),
                rate_limit: getObject(reverseProxyRateLimitConfig),
            } : undefined;

            const cfgWebUiConfig: WebUiConfig = {
                enabled: webUiConfig.enabled ?? false,
                user_ui_enabled: webUiConfig.user_ui_enabled ?? false,
                path: webUiConfig.path,
                auth: webAuthConfig,
            }

            const cfgVideo = getObject(videoConfig) && getObject(videoDownloadConfig) ? {
                ...videoConfig,
                download: getObject(videoDownloadConfig)
            }: undefined;

            const cfgLog = {
                ...logConfig
            };

            const cfgMain: ServerMainConfig = {
                api: apiConfig,
                working_dir: mainConfig.working_dir,
                backup_dir: mainConfig.backup_dir,
                schedules: mainConfig.schedules?.filter(s => s.schedule?.trim().length),
                threads: mainConfig.threads,
                messaging: getObject(cfgMessaging),
                reverse_proxy: getObject(cfgReverseProxy),
                video: getObject(cfgVideo),
                log: getObject(cfgLog),
                update_on_boot: mainConfig.update_on_boot,
                web_ui: getObject(cfgWebUiConfig),
            };

            services.config().saveMainConfig(cfgMain).subscribe({
                next: () => enqueueSnackbar(translate('MESSAGES.SAVE.MAIN_CONFIG.SUCCESS'), {variant: 'success'}),
                error: (err: any) => enqueueSnackbar(translate('MESSAGES.SAVE.MAIN_CONFIG.FAIL'), {variant: 'error'})
            });
        }
    }, [mainConfig, apiConfig, videoConfig, messagingConfig, telegramConfig, restConfig, pushoverConfig,
        videoDownloadConfig, reverseProxyConfig, enqueueSnackbar, services, logConfig,
        reverseProxyRateLimitConfig, reverseProxyCacheConfig, reverseProxyStreamConfig, reverseProxyStreamBufferConfig,
        webUiConfig, webAuthConfig,
        translate]);

    const handleTabChange = useCallback((tab: string) => {
        setActiveTab(tab);
    }, []);


    return <div className={'main-config'}>
        <div className={'main-config__toolbar'}><label>{translate('LABEL.CONFIG')}</label>
            <button data-tooltip='LABEL.SAVE' onClick={handleSave}>{translate('LABEL.SAVE')}</button>
        </div>
        <TabSet tabs={configs.tabs} active={activeTab} onTabChange={handleTabChange}></TabSet>
        <div className={'main-config__content'}>
            <div className={'main-config__content-form' + ('api' !== activeTab ? ' hidden' : '')}>
                <FormView data={apiConfig} fields={configs.api_fields}></FormView>
                <div className={'main-config__content-help'}>
                    <span>{translate('INFO.WEB_ROOT')}</span>
                </div>
            </div>
            <div className={'main-config__content-form main-config__form' + ('main' !== activeTab ? ' hidden' : '')}>
                <FormView data={mainConfig} fields={configs.fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.LOG')}</label>
                <FormView data={logConfig} fields={configs.log_fields}></FormView>
                <div className={'main-config__content-help'}>
                    <span>{translate('INFO.THREADS')}</span>
                </div>
            </div>
            <div className={'main-config__content-form web_ui__form' + ('web_ui' !== activeTab ? ' hidden' : '')}>
                <FormView data={webUiConfig} fields={configs.web_ui_config_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.WEB_AUTH')}</label>
                <FormView data={webAuthConfig} fields={configs.web_auth_config_fields}></FormView>
            </div>
            <div
                className={'main-config__content-form schedules__form' + ('schedules' !== activeTab ? ' hidden' : '')}>
                <FormView data={mainConfig} fields={configs.schedules_fields}></FormView>
                <div className={'main-config__content-help'}>
                    <span>{translate('INFO.SCHEDULE_EXAMPLE')}:</span>
                    {scheduleImage}
                </div>
            </div>
            <div
                className={'main-config__content-form reverse-proxy__form' + ('reverseProxy' !== activeTab ? ' hidden' : '')}>
                <FormView data={reverseProxyConfig} fields={configs.reverse_proxy_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.STREAM')}</label>
                <FormView data={reverseProxyStreamConfig} fields={configs.reverse_proxy_stream_fields}></FormView>
                <FormView data={reverseProxyStreamBufferConfig}
                          fields={configs.reverse_proxy_stream_buffer_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.CACHE')}</label>
                <FormView data={reverseProxyCacheConfig} fields={configs.reverse_proxy_cache_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.RATE_LIMIT')}</label>
                <FormView data={reverseProxyRateLimitConfig}
                          fields={configs.reverse_proxy_rate_limit_fields}></FormView>
            </div>
            <div className={'main-config__content-form' + ('messaging' !== activeTab ? ' hidden' : '')}>
                <FormView data={messagingConfig} fields={configs.messaging_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.TELEGRAM')}</label>
                <FormView data={telegramConfig} fields={configs.telegram_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.REST')}</label>
                <FormView data={restConfig} fields={configs.rest_fields}></FormView>
                <label className="main-config__content-form__section-title">{translate('LABEL.PUSHOVER')}</label>
                <FormView data={pushoverConfig} fields={configs.pushover_fields}></FormView>
            </div>
            <div className={'main-config__content-form' + ('video' !== activeTab ? ' hidden' : '')}>
                <FormView data={videoConfig} fields={configs.video_fields}></FormView>
                <FormView data={videoDownloadConfig} fields={configs.video_download_fields}></FormView>
                <div className={'main-config__content-help'}>
                    <span>{translate('LABEL.EXAMPLES')}:</span>
                    <span>{translate('LABEL.WEB_SEARCH_URL')}: <pre>{'https://www.imdb.com/search/title/?title={}'}</pre></span>
                    <span>{translate('LABEL.EPISODE_PATTERN')}: <pre>{'.*(?P<episode>[Ss]\\d{1,2}(.*?)[Ee]\\d{1,2}).*'}</pre></span>
                </div>

            </div>
        </div>

        <div className={'main-config__restart'}>
            <span className="main-config__restart-warn-icon">{getIconByName('Warn')}</span>
            <span>{translate('INFO.RESTART_TO_APPLY_CHANGES')}</span>
        </div>
    </div>
}